import{_ as i}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as s,c as n,a,b as h,d as r,r as o}from"./app.e9ed1342.js";const l={},c=r('<h1 id="\u67B6\u6784-\u7406\u89E3\u67B6\u6784\u7684\u6F14\u8FDB" tabindex="-1"><a class="header-anchor" href="#\u67B6\u6784-\u7406\u89E3\u67B6\u6784\u7684\u6F14\u8FDB" aria-hidden="true">#</a> \u67B6\u6784-\u7406\u89E3\u67B6\u6784\u7684\u6F14\u8FDB</h1><blockquote><p>\u5728\u5B66\u4E60\u67B6\u6784\u65F6\uFF0C<strong>\u7B2C\u4E00\u6B65\u4E0D\u8981\u53BB\u5B66\u4E60\u6846\u67B6\uFF0C\u800C\u662F\u8981\u5B66\u4E60\u67B6\u6784\u7684\u6F14\u8FDB</strong>\u3002\u5F3A\u70C8\u63A8\u8350\u674E\u667A\u6167\u8001\u5E08\u7684\u300A\u5927\u578B\u7F51\u7AD9\u6280\u672F\u67B6\u6784\u300B\uFF0C\u8FD9\u672C\u4E66\u7FFB\u8D77\u6765\u5F88\u5FEB\uFF0C\u5BF9\u6784\u7B51\u4F60\u81EA\u5DF1\u7684\u4F53\u7CFB\u5F88\u6709\u5E2E\u52A9\uFF0C\u672C\u6587\u7684\u5185\u5BB9\u6765\u6E90\u4E8E\u5B83\uFF0C\u5728\u6B64\u57FA\u7840\u4E0A\u62D3\u5C55\u4E86\u4E0B</p></blockquote><h2 id="_1-\u67B6\u6784\u6F14\u8FDB" tabindex="-1"><a class="header-anchor" href="#_1-\u67B6\u6784\u6F14\u8FDB" aria-hidden="true">#</a> 1. \u67B6\u6784\u6F14\u8FDB</h2><p>\u5927\u578B\u7F51\u7AD9\u7684\u6280\u672F\u6311\u6218\u4E3B\u8981\u6765\u81EA\u4E8E\u5E9E\u5927\u7684\u7528\u6237\uFF0C\u9AD8\u5E76\u53D1\u7684\u8BBF\u95EE\u548C\u6D77\u91CF\u7684\u6570\u636E\uFF0C\u4EFB\u4F55\u7B80\u5355\u7684\u4E1A\u52A1\u4E00\u65E6\u9700\u8981\u5904\u7406\u6570\u4EE5P\u8BA1\u7684\u6570\u636E\u548C\u9762\u5BF9\u6570\u4EE5\u4EBF\u8BA1\u7684\u7528\u6237\uFF0C\u95EE\u9898\u5C31\u4F1A\u53D8\u5F97\u5F88\u68D8\u624B\u3002\u5927\u578B\u7F51\u7AD9\u67B6\u6784\u4E3B\u8981\u5C31\u662F\u89E3\u51B3\u8FD9\u7C7B\u95EE\u9898\u3002</p><p>\u67B6\u6784\u9009\u578B\u662F\u6839\u636E\u5F53\u524D\u4E1A\u52A1\u9700\u8981\u6765\u7684\uFF0C\u5728\u6EE1\u8DB3\u4E1A\u52A1\u9700\u6C42\u7684\u524D\u63D0\u4E0B\uFF0C\u65E2\u8981\u6709\u8DB3\u591F\u7684\u6269\u5C55\u6027\u4E5F\u4E0D\u80FD\u8FC7\u5EA6\u8BBE\u8BA1\uFF0C\u6BCF\u6B21\u7684\u67B6\u6784\u5347\u7EA7\u90FD\u662F\u4E3A\u4E86\u89E3\u51B3\u7CFB\u7EDF\u74F6\u9888\u800C\u505A\u7684\u3002</p><h3 id="_1-1-\u521D\u59CB\u9636\u6BB5\u7684\u7F51\u7AD9\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_1-1-\u521D\u59CB\u9636\u6BB5\u7684\u7F51\u7AD9\u67B6\u6784" aria-hidden="true">#</a> 1.1 \u521D\u59CB\u9636\u6BB5\u7684\u7F51\u7AD9\u67B6\u6784</h3><p>\u521D\u59CB\u9636\u6BB5\u90FD\u6BD4\u8F83\u7B80\u5355\uFF0C\u901A\u5E38\u4E00\u53F0\u670D\u52A1\u5668\u5C31\u53EF\u4EE5\u641E\u5B9A\u4E00\u4E2A\u7F51\u7AD9\u4E86\uFF0C\u770B\u56FE\u3002</p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704202232501.png" alt="image-20220704202232501"><h3 id="_1-2-\u5E94\u7528\u670D\u52A1\u548C\u6570\u636E\u670D\u52A1\u5206\u79BB" tabindex="-1"><a class="header-anchor" href="#_1-2-\u5E94\u7528\u670D\u52A1\u548C\u6570\u636E\u670D\u52A1\u5206\u79BB" aria-hidden="true">#</a> 1.2 \u5E94\u7528\u670D\u52A1\u548C\u6570\u636E\u670D\u52A1\u5206\u79BB</h3><p>\u968F\u7740\u7F51\u7AD9\u4E1A\u52A1\u7684\u53D1\u5C55\uFF0C\u4E00\u53F0\u670D\u52A1\u5668\u9010\u6E10\u4E0D\u80FD\u6EE1\u8DB3\u9700\u6C42\uFF1B\u8FD9\u65F6\u5019\u5C31\u9700\u8981\u5C06\u5E94\u7528\u548C\u6570\u636E\u5206\u79BB\uFF0C\u5982\u56FE\u3002</p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704202540782.png" alt="image-20220704202540782"><h3 id="_1-3-\u4F7F\u7528\u7F13\u5B58\u6539\u5584\u7F51\u7AD9\u6027\u80FD" tabindex="-1"><a class="header-anchor" href="#_1-3-\u4F7F\u7528\u7F13\u5B58\u6539\u5584\u7F51\u7AD9\u6027\u80FD" aria-hidden="true">#</a> 1.3 \u4F7F\u7528\u7F13\u5B58\u6539\u5584\u7F51\u7AD9\u6027\u80FD</h3><p>\u6BEB\u65E0\u7591\u95EE\uFF0C\u73B0\u5728\u7684\u7F51\u7AD9\u57FA\u672C\u4E0A\u90FD\u4F1A\u4F7F\u7528\u7F13\u5B58\uFF0C\u5373\uFF1A80%\u7684\u4E1A\u52A1\u8BBF\u95EE\u90FD\u4F1A\u96C6\u4E2D\u572820%\u7684\u6570\u636E\u4E0A\u3002</p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704202615168.png" alt="image-20220704202615168"><h3 id="_1-4-\u4F7F\u7528\u5E94\u7528\u670D\u52A1\u5668\u96C6\u7FA4\u6539\u5584\u7F51\u7AD9\u7684\u5E76\u53D1\u5904\u7406\u80FD\u529B" tabindex="-1"><a class="header-anchor" href="#_1-4-\u4F7F\u7528\u5E94\u7528\u670D\u52A1\u5668\u96C6\u7FA4\u6539\u5584\u7F51\u7AD9\u7684\u5E76\u53D1\u5904\u7406\u80FD\u529B" aria-hidden="true">#</a> 1.4 \u4F7F\u7528\u5E94\u7528\u670D\u52A1\u5668\u96C6\u7FA4\u6539\u5584\u7F51\u7AD9\u7684\u5E76\u53D1\u5904\u7406\u80FD\u529B</h3><p>\u56E0\u4E3A\u5355\u4E00\u5E94\u7528\u670D\u52A1\u5668\u80FD\u591F\u5904\u7406\u7684\u8BF7\u6C42\u8FDE\u63A5\u6709\u9650\uFF0C\u5728\u7F51\u7AD9\u8BBF\u95EE\u9AD8\u5CF0\u65F6\u671F\uFF0C\u5E94\u7528\u670D\u52A1\u5668\u4F1A\u6210\u4E3A\u6574\u4E2A\u7F51\u7AD9\u7684\u74F6\u9888\u3002\u56E0\u6B64\u4F7F\u7528\u8D1F\u8F7D\u5747\u8861\u5904\u7406\u5668\u52BF\u5728\u5FC5\u7136\u3002\u901A\u8FC7\u8D1F\u8F7D\u5747\u8861\u8C03\u5EA6\u670D\u52A1\u5668\uFF0C\u53EF\u5C06\u6765\u81EA\u6D4F\u89C8\u5668\u7684\u8BBF\u95EE\u8BF7\u6C42\u5206\u53D1\u5230\u5E94\u7528\u7684\u96C6\u7FA4\u4E2D\u7684\u4EFB\u4F55\u4E00\u53F0\u670D\u52A1\u5668\u4E0A\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704202752594.png" alt="image-20220704202752594" loading="lazy"></p><h3 id="_1-5-\u6570\u636E\u5E93\u8BFB\u5199\u5206\u79BB" tabindex="-1"><a class="header-anchor" href="#_1-5-\u6570\u636E\u5E93\u8BFB\u5199\u5206\u79BB" aria-hidden="true">#</a> 1.5 \u6570\u636E\u5E93\u8BFB\u5199\u5206\u79BB</h3><p>\u5F53\u7528\u6237\u8FBE\u5230\u4E00\u5B9A\u89C4\u6A21\u540E\uFF0C\u6570\u636E\u5E93\u56E0\u4E3A\u8D1F\u8F7D\u538B\u529B\u8FC7\u9AD8\u800C\u6210\u4E3A\u7F51\u7AD9\u7684\u74F6\u9888\u3002\u800C\u76EE\u524D\u4E3B\u6D41\u7684\u6570\u636E\u5E93\u90FD\u63D0\u4F9B\u4E3B\u4ECE\u70ED\u5907\u529F\u80FD\uFF0C\u901A\u8FC7\u914D\u7F6E\u4E24\u53F0\u6570\u636E\u5E93\u4E3B\u4ECE\u5173\u7CFB\uFF0C\u53EF\u4EE5\u5C06\u4E00\u53F0\u6570\u636E\u5E93\u7684\u6570\u636E\u66F4\u65B0\u540C\u6B65\u5230\u53E6\u4E00\u53F0\u670D\u52A1\u5668\u4E0A\u3002\u7F51\u7AD9\u5229\u7528\u6570\u636E\u5E93\u8FD9\u4E00\u529F\u80FD\u5B9E\u73B0\u6570\u636E\u5E93\u8BFB\u5199\u5206\u79BB\uFF0C\u4ECE\u800C\u6539\u5584\u6570\u636E\u5E93\u8D1F\u8F7D\u538B\u529B\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704202808118.png" alt="image-20220704202808118" loading="lazy"></p><h3 id="_1-6-\u4F7F\u7528\u53CD\u5411\u4EE3\u7406\u548Ccdn\u52A0\u901F\u7F51\u7AD9\u54CD\u5E94" tabindex="-1"><a class="header-anchor" href="#_1-6-\u4F7F\u7528\u53CD\u5411\u4EE3\u7406\u548Ccdn\u52A0\u901F\u7F51\u7AD9\u54CD\u5E94" aria-hidden="true">#</a> 1.6 \u4F7F\u7528\u53CD\u5411\u4EE3\u7406\u548CCDN\u52A0\u901F\u7F51\u7AD9\u54CD\u5E94</h3><p>\u63D0\u9AD8\u7F51\u7AD9\u7684\u8BBF\u95EE\u901F\u5EA6\uFF0C\u4E3B\u8981\u624B\u6BB5\u6709\u4F7F\u7528CDN\u548C\u53CD\u5411\u4EE3\u7406\u3002</p><p>CDN\u548C\u53CD\u5411\u4EE3\u7406\u7684\u57FA\u672C\u539F\u7406\u90FD\u662F\u7F13\u5B58\uFF0C\u533A\u522B\u5728\u4E8ECDN\u90E8\u7F72\u5728\u7F51\u7EDC\u63D0\u4F9B\u5546\u7684\u673A\u623F\uFF0C\u800C\u53CD\u5411\u4EE3\u7406\u662F\u90E8\u7F72\u5728\u7F51\u7AD9\u7684\u4E2D\u5FC3\u673A\u623F\uFF0C\u5F53\u7528\u6237\u8BF7\u6C42\u5230\u8FBE\u4E2D\u5FC3\u673A\u623F\u540E\uFF0C\u9996\u5148\u8BBF\u95EE\u7684\u53CD\u5411\u4EE3\u7406\uFF0C\u5982\u679C\u53CD\u5411\u4EE3\u7406\u7F13\u5B58\u7740\u7528\u6237\u8BF7\u6C42\u7684\u8D44\u6E90\uFF0C\u5219\u76F4\u63A5\u8FD4\u56DE\u7ED9\u7528\u6237\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704202934009.png" alt="image-20220704202934009" loading="lazy"></p><h3 id="_1-7-\u4F7F\u7528\u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF\u548C\u5206\u5E03\u5F0F\u6570\u636E\u5E93\u7CFB\u7EDF" tabindex="-1"><a class="header-anchor" href="#_1-7-\u4F7F\u7528\u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF\u548C\u5206\u5E03\u5F0F\u6570\u636E\u5E93\u7CFB\u7EDF" aria-hidden="true">#</a> 1.7 \u4F7F\u7528\u5206\u5E03\u5F0F\u6587\u4EF6\u7CFB\u7EDF\u548C\u5206\u5E03\u5F0F\u6570\u636E\u5E93\u7CFB\u7EDF</h3><p>\u4EFB\u4F55\u5F3A\u5927\u7684\u5355\u4E00\u670D\u52A1\u5668\u90FD\u6EE1\u8DB3\u4E0D\u4E86\u5927\u578B\u7F51\u7AD9\u6301\u7EED\u589E\u957F\u7684\u4E1A\u52A1\u9700\u6C42\u3002</p><p>\u5206\u5E03\u5F0F\u6570\u636E\u5E93\u65F6\u7F51\u7AD9\u6570\u636E\u5E93\u62C6\u5206\u7684\u6700\u540E\u624B\u6BB5\uFF0C\u53EA\u7528\u5728\u5355\u8868\u6570\u636E\u89C4\u6A21\u975E\u5E38\u5927\u7684\u65F6\u5019\u624D\u4F7F\u7528\u3002\u4E0D\u5230\u4E0D\u5F97\u5DF2\u65F6\uFF0C\u7F51\u7AD9\u66F4\u5E38\u7528\u7684\u6570\u636E\u5E93\u62C6\u5206\u624B\u6BB5\u662F\u4E1A\u52A1\u62C6\u5206\uFF0C\u5C06\u4E0D\u540C\u4E1A\u52A1\u7684\u6570\u636E\u90E8\u7F72\u5728\u4E0D\u540C\u7684\u7269\u7406\u670D\u52A1\u5668\u4E0A\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704203026849.png" alt="image-20220704203026849" loading="lazy"></p><h3 id="_1-8-\u4F7F\u7528nosql\u548C\u641C\u7D22\u5F15\u64CE" tabindex="-1"><a class="header-anchor" href="#_1-8-\u4F7F\u7528nosql\u548C\u641C\u7D22\u5F15\u64CE" aria-hidden="true">#</a> 1.8 \u4F7F\u7528NoSQL\u548C\u641C\u7D22\u5F15\u64CE</h3><p>\u641C\u7D22\u5F15\u64CE\u4E5F\u57FA\u672C\u5DF2\u7ECF\u5F62\u6210\u73B0\u5728\u5927\u578B\u7F51\u7AD9\u5FC5\u987B\u63D0\u4F9B\u7684\u529F\u80FD\u4E86\uFF0C\u7F51\u7AD9\u9700\u8981\u91C7\u7528\u4E00\u4E9B\u975E\u5173\u7CFB\u6570\u636E\u5E93\u6280\u672F\u5982NoSQL\u548C\u975E\u6570\u636E\u5E93\u67E5\u8BE2\u6280\u672F\u5982\u641C\u7D22\u5F15\u64CE\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704203106737.png" alt="image-20220704203106737" loading="lazy"></p><h3 id="_1-9-\u4E1A\u52A1\u62C6\u5206" tabindex="-1"><a class="header-anchor" href="#_1-9-\u4E1A\u52A1\u62C6\u5206" aria-hidden="true">#</a> 1.9 \u4E1A\u52A1\u62C6\u5206</h3><p>\u5927\u578B\u7F51\u7AD9\u4E3A\u4E86\u5E94\u5BF9\u65E5\u76CA\u590D\u6742\u7684\u4E1A\u52A1\u573A\u666F\uFF0C\u901A\u8FC7\u4F7F\u7528\u5206\u800C\u6CBB\u4E4B\u7684\u624B\u6BB5\u5C06\u6574\u4E2A\u7F51\u7AD9\u4E1A\u52A1\u62C6\u5206\u6210\u4E0D\u540C\u7684\u4EA7\u54C1\u7EBF\u3002</p><p>\u5177\u4F53\u5230\u6280\u672F\u4E0A\uFF0C\u4E5F\u4F1A\u6839\u636E\u4EA7\u54C1\u7EBF\u5212\u5206\uFF0C\u5C06\u4E00\u4E2A\u7F51\u7AD9\u62C6\u5206\u6210\u8BB8\u591A\u4E0D\u540C\u7684\u5E94\u7528\uFF0C\u6BCF\u4E2A\u5E94\u7528\u72EC\u7ACB\u90E8\u7F72\u7EF4\u62A4\u3002\u5E94\u7528\u4E4B\u95F4\u53EF\u4EE5\u901A\u8FC7\u8D85\u94FE\u63A5\u5EFA\u7ACB\u7BA1\u7406\uFF0C\u4E5F\u53EF\u4EE5\u901A\u8FC7\u6D88\u606F\u961F\u5217\u8FDB\u884C\u6570\u636E\u5206\u53D1\uFF0C<strong>\u5F53\u7136\u6700\u591A\u7684\u8FD8\u662F\u901A\u8FC7\u8BBF\u95EE\u540C\u4E00\u4E2A\u6570\u636E\u5B58\u50A8\u7CFB\u7EDF\u6765\u6784\u6210\u4E00\u4E2A\u5173\u8054\u7684\u5B8C\u6574\u7CFB\u7EDF</strong>\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704203305103.png" alt="image-20220704203305103" loading="lazy"></p><h3 id="_1-10-\u5206\u5E03\u5F0F\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#_1-10-\u5206\u5E03\u5F0F\u670D\u52A1" aria-hidden="true">#</a> 1.10 \u5206\u5E03\u5F0F\u670D\u52A1</h3><p>\u7531\u4E8E\u6BCF\u4E00\u4E2A\u5E94\u7528\u7CFB\u7EDF\u90FD\u9700\u8981\u6267\u884C\u8BB8\u591A\u76F8\u540C\u7684\u4E1A\u52A1\u64CD\u4F5C\uFF0C\u6BD4\u5982\u7528\u6237\u7BA1\u7406\uFF0Csession\u7BA1\u7406\uFF0C\u90A3\u4E48\u53EF\u4EE5\u5C06\u8FD9\u4E9B\u516C\u7528\u7684\u4E1A\u52A1\u63D0\u53D6\u51FA\u6765\uFF0C\u72EC\u7ACB\u90E8\u7F72\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704203400514.png" alt="image-20220704203400514" loading="lazy"></p><h2 id="_2-\u793A\u4F8B-\u7535\u5546\u7CFB\u7EDF\u67B6\u6784\u6F14\u8FDB" tabindex="-1"><a class="header-anchor" href="#_2-\u793A\u4F8B-\u7535\u5546\u7CFB\u7EDF\u67B6\u6784\u6F14\u8FDB" aria-hidden="true">#</a> 2. \u793A\u4F8B:\u7535\u5546\u7CFB\u7EDF\u67B6\u6784\u6F14\u8FDB</h2><p>\u5177\u4F53\u4EE5\u7535\u5B50\u5546\u52A1\u7F51\u7AD9\u4E3A\u4F8B\uFF0C \u5C55\u793Aweb\u5E94\u7528\u7684\u67B6\u6784\u6F14\u53D8\u8FC7\u7A0B\u3002</p><h3 id="_1-0\u65F6\u4EE3" tabindex="-1"><a class="header-anchor" href="#_1-0\u65F6\u4EE3" aria-hidden="true">#</a> 1.0\u65F6\u4EE3</h3><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704203436208.png" alt="image-20220704203436208"><p>\u8FD9\u4E2A\u65F6\u5019\u662F\u4E00\u4E2Aweb\u9879\u76EE\u91CC\u5305\u542B\u4E86\u6240\u6709\u7684\u6A21\u5757\uFF0C\u4E00\u4E2A\u6570\u636E\u5E93\u91CC\u5305\u542B\u4E86\u6240\u9700\u8981\u7684\u6240\u6709\u8868\uFF0C\u8FD9\u65F6\u5019\u7F51\u7AD9\u8BBF\u95EE\u91CF\u589E\u52A0\u65F6\uFF0C\u9996\u5148\u9047\u5230\u74F6\u9888\u7684\u662F\u5E94\u7528\u670D\u52A1\u5668\u8FDE\u63A5\u6570\uFF0C\u6BD4\u5982tomcat\u8FDE\u63A5\u6570\u4E0D\u80FD\u65E0\u9650\u589E\u52A0\uFF0C\u7EBF\u7A0B\u6570\u4E0A\u9650\u53D7\u8FDB\u7A0B\u5185\u5B58\u5927\u5C0F\u3001CPU\u5185\u6838\u6570\u7B49\u56E0\u7D20\u5F71\u54CD\uFF0C\u5F53\u7EBF\u7A0B\u6570\u5230\u8FBE\u4E00\u5B9A\u6570\u65F6\u5019\uFF0C\u7EBF\u7A0B\u4E0A\u4E0B\u6587\u7684\u5207\u6362\u5BF9\u6027\u80FD\u7684\u635F\u8017\u4F1A\u8D8A\u6765\u8D8A\u4E25\u91CD\uFF0C\u54CD\u5E94\u4F1A\u53D8\u6162\uFF0C\u901A\u8FC7\u589E\u52A0web\u5E94\u7528\u670D\u52A1\u5668\u65B9\u5F0F\u7684\u6A2A\u5411\u6269\u5C55\u5BF9\u67B6\u6784\u5F71\u54CD\u6700\u5C0F\uFF0C\u8FD9\u65F6\u5019\u67B6\u6784\u4F1A\u53D8\u6210\u4E0B\u9762\u8FD9\u6837\uFF1A</p><h3 id="_2-0\u65F6\u4EE3" tabindex="-1"><a class="header-anchor" href="#_2-0\u65F6\u4EE3" aria-hidden="true">#</a> 2.0\u65F6\u4EE3</h3><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704203621715.png" alt="image-20220704203621715"><p>\u8FD9\u65F6\u5019\u968F\u7740\u7F51\u7AD9\u8BBF\u95EE\u91CF\u7EE7\u7EED\u589E\u52A0\uFF0C\u7EE7\u7EED\u589E\u52A0\u5E94\u7528\u670D\u52A1\u5668\u6570\u91CF\u540E\u53D1\u73B0\u6570\u636E\u5E93\u6210\u4E86\u74F6\u9888\uFF0C\u800C\u6570\u636E\u5E93\u7684\u6700\u4E3B\u8981\u7684\u74F6\u9888\u4F53\u73B0\u5728\u4E24\u65B9\u9762\uFF1A</p><ul><li>\u6570\u636E\u5E93\u7684\u6700\u5927\u8FDE\u63A5\u6570\u662F\u6709\u9650\u7684\uFF0C\u6BD4\u5982\u5F53\u524D\u6570\u636E\u5E93\u7684\u8FDE\u63A5\u6570\u8BBE\u7F6E8000\uFF0C\u5982\u679C\u6BCF\u4E2A\u5E94\u7528\u670D\u52A1\u5668\u4E0E\u6570\u636E\u5E93\u7684\u521D\u59CB\u8FDE\u63A5\u6570\u8BBE\u7F6E40\uFF0C\u90A3\u4E48200\u53F0web\u670D\u52A1\u5668\u662F\u6781\u9650\uFF0C \u5E76\u4E14\u8FDE\u63A5\u6570\u592A\u591A\u540E\uFF0C\u6570\u636E\u5E93\u7684\u8BFB\u5199\u538B\u529B\u589E\u5927\uFF0C\u8017\u65F6\u589E\u52A0</li><li>\u5F53\u5355\u8868\u6570\u91CF\u8FC7\u5927\u65F6\uFF0C\u5BF9\u8BE5\u8868\u7684\u64CD\u4F5C\u8017\u65F6\u4F1A\u589E\u52A0\uFF0C\u7D22\u5F15\u4F18\u5316\u4E5F\u662F\u7F13\u5175\u4E4B\u8BA1</li></ul><p>\u8FD9\u65F6\uFF0C\u6839\u636E\u4E1A\u52A1\u7279\u70B9\uFF0C\u5982\u679C\u8BFB\u5199\u6BD4\u5DEE\u8DDD\u4E0D\u5927\uFF0C\u5E76\u4E14\u5BF9\u6570\u636E\u4E00\u81F4\u6027\u8981\u6C42\u4E0D\u662F\u5F88\u9AD8\u7684\u60C5\u51B5\u4E0B\uFF0C\u6570\u636E\u5E93\u53EF\u4EE5\u91C7\u7528\u4E3B\u4ECE\u65B9\u5F0F\u8FDB\u884C\u8BFB\u5199\u5206\u79BB\u7684\u65B9\u6848\uFF0C\u5E76\u4E14\u5F15\u5165\u7F13\u5B58\u673A\u5236\u6765\u6297\u8BFB\u6D41\u91CF\u3002\u5982\u679C\u8BFB\u5199\u6BD4\u5DEE\u8DDD\u5F88\u5927\u6216\u8005\u5BF9\u6570\u636E\u4E00\u81F4\u6027\u8981\u6C42\u9AD8\u65F6\uFF0C\u5C31\u4E0D\u9002\u5408\u7528\u8BFB\u5199\u5206\u79BB\u65B9\u6848\uFF0C\u9700\u8981\u8003\u8651\u4E1A\u52A1\u7684\u5782\u76F4\u62C6\u5206\uFF0C\u8FD9\u65F6\u671F\u7684\u7CFB\u7EDF\u67B6\u6784\u56FE\u5982\u4E0B\uFF1A</p><h3 id="_3-0\u65F6\u4EE3" tabindex="-1"><a class="header-anchor" href="#_3-0\u65F6\u4EE3" aria-hidden="true">#</a> 3.0\u65F6\u4EE3</h3><h4 id="_3-1-\u8BFB\u5199\u5206\u79BB" tabindex="-1"><a class="header-anchor" href="#_3-1-\u8BFB\u5199\u5206\u79BB" aria-hidden="true">#</a> 3.1 \u8BFB\u5199\u5206\u79BB</h4><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704203808145.png" alt="image-20220704203808145"><p>\u8FD9\u65F6\u5019\u4ECD\u7136\u662F\u5782\u76F4\u67B6\u6784\uFF0C\u6240\u6709\u4E1A\u52A1\u96C6\u4E2D\u5728\u4E00\u4E2A\u9879\u76EE\u91CC\u3002\u9879\u76EE\u7EF4\u62A4\u3001\u5FEB\u901F\u8FED\u4EE3\u95EE\u9898\u4F1A\u8D8A\u6765\u8D8A\u4E25\u91CD\uFF0C\u5355\u4E2A\u6A21\u5757\u7684\u5F00\u53D1\u90FD\u9700\u8981\u53D1\u5E03\u6574\u4E2A\u9879\u76EE\uFF0C\u9879\u76EE\u7A33\u5B9A\u6027\u4E5F\u53D7\u5230\u5F88\u5927\u6311\u6218\uFF0C\u8FD9\u662F\u9700\u8981\u8003\u8651\u4E1A\u52A1\u7684\u5782\u76F4\u62C6\u5206\uFF0C\u9700\u8981\u5C06\u4E00\u4E9B\u5927\u7684\u6A21\u5757\u5355\u72EC\u62C6\u51FA\u6765\uFF0C\u8FD9\u65F6\u5019\u7684\u67B6\u6784\u56FE\u5982\u4E0B\uFF1A</p><h3 id="_4-0-\u4E1A\u52A1\u5782\u76F4\u62C6\u5206" tabindex="-1"><a class="header-anchor" href="#_4-0-\u4E1A\u52A1\u5782\u76F4\u62C6\u5206" aria-hidden="true">#</a> 4.0 \u4E1A\u52A1\u5782\u76F4\u62C6\u5206</h3><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704203919468.png" alt="image-20220704203919468" loading="lazy"></p><p>\u8FD9\u65F6\u5019\u4E3A\u4E86\u8FDB\u4E00\u6B65\u63D0\u5347\u7528\u6237\u4F53\u9A8C\uFF0C\u52A0\u901F\u7528\u6237\u7684\u7F51\u7AD9\u8BBF\u95EE\u901F\u5EA6\uFF0C\u4F1A\u4F7F\u7528CDN\u6765\u7F13\u5B58\u4FE1\u606F\uFF0C\u7528\u6237\u4F1A\u8BBF\u95EE\u6700\u8FD1\u7684CDN\u8282\u70B9\u6765\u63D0\u5347\u8BBF\u95EE\u901F\u5EA6\u3002\u6B64\u65F6\u7684\u67B6\u6784\u56FE\u5982\u4E0B\uFF1A</p><h4 id="_4-1-\u4F7F\u7528cdn\u6765\u7F13\u5B58\u4FE1\u606F" tabindex="-1"><a class="header-anchor" href="#_4-1-\u4F7F\u7528cdn\u6765\u7F13\u5B58\u4FE1\u606F" aria-hidden="true">#</a> 4.1 \u4F7F\u7528CDN\u6765\u7F13\u5B58\u4FE1\u606F</h4><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704204007659.png" alt="image-20220704204007659"><p>\u968F\u7740\u4E1A\u52A1\u91CF\u589E\u5927\uFF0C\u4E00\u4E9B\u6838\u5FC3\u7CFB\u7EDF\u6570\u636E\u5E93\u5355\u8868\u6570\u91CF\u8FBE\u5230\u51E0\u5343\u4E07\u751A\u81F3\u4EBF\u7EA7\uFF0C\u8FD9\u65F6\u5019\u5BF9\u8BE5\u8868\u7684\u6570\u636E\u64CD\u4F5C\u6548\u7387\u4F1A\u5927\u5927\u964D\u4F4E\uFF0C\u5E76\u4E14\u867D\u7136\u6709\u7F13\u5B58\u6765\u6297\u8BFB\u7684\u538B\u529B\uFF0C\u4F46\u662F\u5BF9\u4E8E\u5927\u91CF\u7684\u5199\u64CD\u4F5C\u548C\u4E00\u4E9B\u7F13\u5B58miss\u7684\u6D41\u91CF\u5230\u8FBE\u4E00\u5B9A\u91CF\u65F6\uFF0C\u5355\u5E93\u7684\u8D1F\u8377\u4E5F\u4F1A\u5230\u8FBE\u6781\u9650\uFF0C\u8FD9\u65F6\u5019\u9700\u8981\u5C06\u8868\u62C6\u5206\uFF0C\u4E00\u822C\u76F4\u63A5\u91C7\u7528\u5206\u5E93\u5206\u8868\uFF0C\u56E0\u4E3A\u53EA\u505A\u5206\u8868\u7684\u8BDD\uFF0C\u5355\u4E2A\u5E93\u7684\u8FDE\u63A5\u74F6\u9888\u4ECD\u7136\u65E0\u6CD5\u89E3\u51B3\u3002\u5206\u5E93\u5206\u8868\u540E\u7684\u67B6\u6784\u5982\u4E0B\uFF1A</p><h4 id="_4-2\u5206\u5E93\u5206\u8868\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_4-2\u5206\u5E93\u5206\u8868\u67B6\u6784" aria-hidden="true">#</a> 4.2\u5206\u5E93\u5206\u8868\u67B6\u6784</h4><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704204058186.png" alt="image-20220704204058186" loading="lazy"></p><p>\u968F\u7740\u6D41\u91CF\u7684\u8FDB\u4E00\u6B65\u589E\u5927\uFF0C\u8FD9\u65F6\u5019\u7CFB\u7EDF\u4ECD\u7136\u4F1A\u6709\u74F6\u9888\u51FA\u73B0\uFF0C\u4EE5\u8BA2\u5355\u7CFB\u7EDF\u4E3A\u4F8B\uFF1A \u5355\u4E2A\u673A\u623F\u7684\u673A\u5668\u662F\u6709\u9650\u7684\uFF0C\u4E0D\u80FD\u4E00\u76F4\u65B0\u589E\u4E0B\u53BB\uFF0C\u5E76\u4E14\u57FA\u4E8E\u5BB9\u707E\u7684\u8003\u8651\uFF0C\u4E00\u822C\u91C7\u7528\u540C\u57CE\u53CC\u673A\u623F\u7684\u65B9\u5F0F\uFF0C\u673A\u623F\u4E4B\u95F4\u7528\u4E13\u7EBF\u94FE\u63A5\uFF0C\u540C\u57CE\u8DE8\u673A\u623F\u8D28\u68C0\u7684\u5EF6\u65F6\u5728\u51E0\u6BEB\u79D2\uFF0C\u6B64\u65F6\u7684\u67B6\u6784\u56FE\u5982\u4E0B\uFF1A</p><h4 id="_4-3-\u540C\u57CE\u53CC\u673A\u623F" tabindex="-1"><a class="header-anchor" href="#_4-3-\u540C\u57CE\u53CC\u673A\u623F" aria-hidden="true">#</a> 4.3 \u540C\u57CE\u53CC\u673A\u623F</h4><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704204203286.png" alt="image-20220704204203286" loading="lazy"></p><p>\u7531\u4E8E\u6570\u636E\u5E93\u4E3B\u5E93\u53EA\u80FD\u662F\u5728\u4E00\u4E2A\u673A\u623F\uFF0C\u6240\u4EE5\u4ECD\u7136\u4F1A\u6709\u4E00\u534A\u7684\u6570\u636E\u5E93\u8BBF\u95EE\u662F\u8DE8\u673A\u623F\u7684\uFF0C\u867D\u7136\u5EF6\u65F6\u53EA\u6709\u51E0\u6BEB\u79D2\uFF0C\u4F46\u662F\u4E00\u4E2A\u8C03\u7528\u94FE\u91CC\u7684\u6570\u636E\u5E93\u8BBF\u95EE\u592A\u591A\u540E\uFF0C\u8FD9\u4E2A\u5EF6\u65F6\u4E5F\u4F1A\u79EF\u5C11\u6210\u591A\u3002\u5176\u6B21\u8FD9\u4E2A\u67B6\u6784\u8FD8\u662F\u6CA1\u80FD\u89E3\u51B3\u6570\u636E\u5E93\u8FDE\u63A5\u6570\u74F6\u9888\u95EE\u9898</p><ul><li>\u968F\u7740\u5E94\u7528\u670D\u52A1\u5668\u7684\u589E\u52A0\uFF0C\u867D\u7136\u662F\u5206\u5E93\u5206\u8868\uFF0C\u4F46\u6BCF\u589E\u52A0\u4E00\u53F0\u5E94\u7528\u670D\u52A1\u5668\uFF0C\u90FD\u4F1A\u4E0E\u6BCF\u4E2A\u5206\u5E93\u5EFA\u7ACB\u8FDE\u63A5\uFF0C\u6BD4\u5982\u6570\u636E\u5E93\u8FDE\u63A5\u6C60\u9ED8\u8BA4\u8FDE\u63A5\u6570\u662F40\uFF0C\u800C\u5982\u679Cmysql\u6570\u636E\u5E93\u7684\u6700\u5927\u8FDE\u63A5\u6570\u662F8000\u7684\u8BDD\uFF0C\u90A3\u4E48200\u53F0\u5E94\u7528\u670D\u52A1\u5668\u5C31\u662F\u6781\u9650\u3002</li><li>\u5F53\u5E94\u7528\u7684\u91CF\u7EA7\u592A\u5927\u540E\uFF0C\u5355\u4E2A\u57CE\u5E02\u7684\u673A\u5668\u3001\u7535\u3001\u5E26\u5BBD\u7B49\u8D44\u6E90\u65E0\u6CD5\u6EE1\u8DB3\u4E1A\u52A1\u7684\u6301\u7EED\u589E\u957F\u3002\u8FD9\u65F6\u5C31\u9700\u8981\u8003\u8651SET\u5316\u67B6\u6784\uFF0C\u4E5F\u5C31\u662F\u5355\u5143\u5316\u67B6\u6784\uFF0C\u5927\u4F53\u601D\u8DEF\u5C31\u662F\u5C06\u4E00\u4E9B\u6838\u5FC3\u7CFB\u7EDF\u62C6\u6210\u591A\u4E2A\u4E2D\u5FC3\uFF0C\u6BCF\u4E2A\u4E2D\u5FC3\u6210\u4E3A\u4E00\u4E2A\u5355\u5143\uFF0C\u6D41\u91CF\u4F1A\u6309\u7167\u4E00\u5B9A\u7684\u89C4\u5219\u5206\u914D\u7ED9\u6BCF\u4E2A\u5355\u5143\uFF0C\u8FD9\u6837\u6BCF\u4E2A\u5355\u5143\u53EA\u8D1F\u8D23\u5904\u7406\u81EA\u5DF1\u7684\u6D41\u91CF\u5C31\u53EF\u4EE5\u4E86\u3002\u6BCF\u4E2A\u5355\u5143\u8981\u5C3D\u91CF\u81EA\u5305\u542B\u3001\u9AD8\u5185\u805A\u3002\u8FD9\u662F\u4ECE\u6574\u4F53\u5C42\u9762\u5C06\u6D41\u91CF\u5206\u800C\u6CBB\u4E4B\u7684\u601D\u8DEF\u3002\u8FD9\u662F\u5355\u5143\u5316\u540E\u7684\u673A\u6784\u7B80\u56FE\u5982\u4E0B\uFF1A</li></ul><h3 id="_5-0-\u5355\u5143\u5316" tabindex="-1"><a class="header-anchor" href="#_5-0-\u5355\u5143\u5316" aria-hidden="true">#</a> 5.0 \u5355\u5143\u5316</h3><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704204400586.png" alt="image-20220704204400586" loading="lazy"></p><p>\u4ECE\u4E0A\u9762\u7684\u67B6\u6784\u56FE\u91CC\u80FD\u770B\u5230\uFF0C\u6D41\u91CF\u4ECE\u63A5\u5165\u5C42\u6309\u7167\u8DEF\u7531\u89C4\u5219\uFF08\u6BD4\u5982\u4EE5\u7528\u6237ID\u6765\u8DEF\u7531\uFF09\u8DEF\u7531\u5230\u4E0D\u540C\u5355\u5143\uFF0C\u6BCF\u4E2A\u5355\u5143\u5185\u90FD\u662F\u9AD8\u5185\u805A\uFF0C\u5305\u542B\u4E86\u6838\u5FC3\u7CFB\u7EDF\uFF0C\u6570\u636E\u5C42\u9762\u7684\u5206\u7247\u903B\u8F91\u662F\u4E0E\u63A5\u5165\u5C42\u8DEF\u6709\u903B\u8F91\u4E00\u81F4\uFF0C\u4E5F\u89E3\u51B3\u4E86\u6570\u636E\u5E93\u8FDE\u63A5\u7684\u74F6\u9888\u95EE\u9898\uFF0C\u4F46\u662F\u4E00\u4E9B\u8DE8\u5355\u5143\u7684\u8C03\u7528\u662F\u65E0\u6CD5\u907F\u514D\u7684\uFF0C\u540C\u65F6\u4E5F\u6709\u4E9B\u65E0\u6CD5\u62C6\u5206\u7684\u4E1A\u52A1\u9700\u8981\u653E\u5728\u4E2D\u5FC3\u5355\u5143\uFF0C\u4F9B\u6240\u6709\u5176\u4ED6\u5355\u5143\u8C03\u7528\u3002</p><h2 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h2>',69),t={href:"https://pdai.tech/md/arch/arch-x-evolution.html",target:"_blank",rel:"noopener noreferrer"},d=a("strong",null,"\u67B6\u6784 - \u7406\u89E3\u67B6\u6784\u7684\u6F14\u8FDB",-1);function g(p,m){const e=o("ExternalLinkIcon");return s(),n("div",null,[c,a("p",null,[a("a",t,[d,h(e)])])])}const _=i(l,[["render",g],["__file","arch-x-evolution.html.vue"]]);export{_ as default};
