import{_ as r}from"./_plugin-vue_export-helper.cdc0426e.js";import{o as i,c as l,a as e,b as s,d as n,e as o,r as t}from"./app.80b5c814.js";const d={},h=n('<h1 id="\u5206\u5E03\u5F0F\u3001\u96C6\u7FA4\u3001\u5FAE\u670D\u52A1\u3001soa\u76F8\u5173\u6982\u5FF5" tabindex="-1"><a class="header-anchor" href="#\u5206\u5E03\u5F0F\u3001\u96C6\u7FA4\u3001\u5FAE\u670D\u52A1\u3001soa\u76F8\u5173\u6982\u5FF5" aria-hidden="true">#</a> \u5206\u5E03\u5F0F\u3001\u96C6\u7FA4\u3001\u5FAE\u670D\u52A1\u3001SOA\u76F8\u5173\u6982\u5FF5</h1><h2 id="_1-\u7B80\u4ECB" tabindex="-1"><a class="header-anchor" href="#_1-\u7B80\u4ECB" aria-hidden="true">#</a> 1. \u7B80\u4ECB</h2><blockquote><ul><li><p><strong>\u5206\u5E03\u5F0F</strong>\uFF1A\u4E00\u4E2A\u4E1A\u52A1\u5206\u62C6\u591A\u4E2A\u5B50\u4E1A\u52A1\uFF0C\u90E8\u7F72\u5728\u4E0D\u540C\u7684\u670D\u52A1\u5668\u4E0A\uFF0C<strong>\u89E3\u51B3\u9AD8\u5E76\u53D1\u7684\u95EE\u9898</strong></p></li><li><p><strong>\u96C6\u7FA4</strong>\uFF1A\u540C\u4E00\u4E2A\u4E1A\u52A1\uFF0C\u90E8\u7F72\u5728\u591A\u4E2A\u670D\u52A1\u5668\u4E0A\uFF0C<strong>\u63D0\u9AD8\u7CFB\u7EDF\u53EF\u7528\u6027</strong></p></li><li><p><strong>\u5206\u5E03\u5F0F\u4E2D\u7684\u6BCF\u4E00\u4E2A\u8282\u70B9\uFF0C\u90FD\u53EF\u4EE5\u505A\u96C6\u7FA4\u3002\u800C\u96C6\u7FA4\u5E76\u4E0D\u4E00\u5B9A\u5C31\u662F\u5206\u5E03\u5F0F\u7684\u3002</strong></p></li></ul></blockquote><ul><li><p><strong>\u5206\u5E03\u5F0F</strong>\uFF1A\u4E0D\u540C\u6A21\u5757\u90E8\u7F72\u5728\u4E0D\u540C\u670D\u52A1\u5668\u4E0A</p><p>\u4F5C\u7528\uFF1A\u5206\u5E03\u5F0F\u89E3\u51B3\u7F51\u7AD9\u9AD8\u5E76\u53D1\u5E26\u6765\u95EE\u9898</p></li><li><p><strong>\u96C6\u7FA4</strong>\uFF1A\u591A\u53F0\u670D\u52A1\u5668\u90E8\u7F72\u76F8\u540C\u5E94\u7528\u6784\u6210\u4E00\u4E2A\u96C6\u7FA4</p><p>\u4F5C\u7528\uFF1A\u901A\u8FC7\u8D1F\u8F7D\u5747\u8861\u8BBE\u5907\u5171\u540C\u5BF9\u5916\u63D0\u4F9B\u670D\u52A1</p></li><li><p><strong>SOA</strong>\uFF1A\u4E1A\u52A1\u7CFB\u7EDF\u5206\u89E3\u4E3A\u591A\u4E2A\u7EC4\u4EF6\uFF0C\u8BA9\u6BCF\u4E2A\u7EC4\u4EF6\u90FD\u72EC\u7ACB\u63D0\u4F9B\u79BB\u6563\uFF0C\u81EA\u6CBB\uFF0C\u53EF\u590D\u7528\u7684\u670D\u52A1\u80FD\u529B\uFF0C\u901A\u8FC7\u670D\u52A1\u7684\u7EC4\u5408\u548C\u7F16\u6392\u6765\u5B9E\u73B0\u4E0A\u5C42\u7684\u4E1A\u52A1\u6D41\u7A0B</p><p>\u4F5C\u7528\uFF1A\u7B80\u5316\u7EF4\u62A4,\u964D\u4F4E\u6574\u4F53\u98CE\u9669,\u4F38\u7F29\u7075\u6D3B</p></li><li><p><strong>\u5FAE\u670D\u52A1</strong>\uFF1A\u67B6\u6784\u8BBE\u8BA1\u6982\u5FF5,\u5404\u670D\u52A1\u95F4\u9694\u79BB\uFF08\u5206\u5E03\u5F0F\u4E5F\u662F\u9694\u79BB\uFF09,\u81EA\u6CBB\uFF08\u5206\u5E03\u5F0F\u4F9D\u8D56\u6574\u4F53\u7EC4\u5408\uFF09\u5176\u5B83\u7279\u6027(\u5355\u4E00\u804C\u8D23,\u8FB9\u754C,\u5F02\u6B65\u901A\u4FE1,\u72EC\u7ACB\u90E8\u7F72)\u662F\u5206\u5E03\u5F0F\u6982\u5FF5\u7684\u66F4\u4E25\u683C\u6267\u884CSOA\u5230\u5FAE\u670D\u52A1\u67B6\u6784\u7684\u6F14\u8FDB\u8FC7\u7A0B</p><p>\u4F5C\u7528\uFF1A\u5404\u670D\u52A1\u53EF\u72EC\u7ACB\u5E94\u7528\uFF0C\u7EC4\u5408\u670D\u52A1\u4E5F\u53EF\u7CFB\u7EDF\u5E94\u7528</p></li></ul><h3 id="_1-1-\u5927\u767D\u8BDD\u8BB2\u5206\u5E03\u5F0F-\u96C6\u7FA4" tabindex="-1"><a class="header-anchor" href="#_1-1-\u5927\u767D\u8BDD\u8BB2\u5206\u5E03\u5F0F-\u96C6\u7FA4" aria-hidden="true">#</a> 1.1 \u5927\u767D\u8BDD\u8BB2\u5206\u5E03\u5F0F/\u96C6\u7FA4</h3><p>\u5C0F\u996D\u5E97\u539F\u6765\u53EA\u6709\u4E00\u4E2A\u53A8\u5E08\uFF0C\u5207\u83DC\u6D17\u83DC\u5907\u6599\u7092\u83DC\u5168\u5E72\u3002\u540E\u6765\u5BA2\u4EBA\u591A\u4E86\uFF0C\u53A8\u623F\u4E00\u4E2A\u53A8\u5E08\u5FD9\u4E0D\u8FC7\u6765\uFF0C\u53C8\u8BF7\u4E86\u4E2A\u53A8\u5E08\uFF0C\u4E24\u4E2A\u53A8\u5E08\u90FD\u80FD\u7092\u4E00\u6837\u7684\u83DC\uFF0C\u8FD9<strong>\u4E24\u4E2A\u53A8\u5E08\u7684\u5173\u7CFB\u662F\u96C6\u7FA4</strong>\u3002</p><p>\u4E3A\u4E86\u8BA9\u53A8\u5E08\u4E13\u5FC3\u7092\u83DC\uFF0C\u628A\u83DC\u505A\u5230\u6781\u81F4\uFF0C\u53C8\u8BF7\u4E86\u4E2A\u914D\u83DC\u5E08\u8D1F\u8D23\u5207\u83DC\uFF0C\u5907\u83DC\uFF0C\u5907\u6599\uFF0C<strong>\u53A8\u5E08\u548C\u914D\u83DC\u5E08\u7684\u5173\u7CFB\u662F\u5206\u5E03\u5F0F</strong>\uFF0C\u4E00\u4E2A\u914D\u83DC\u5E08\u4E5F\u5FD9\u4E0D\u8FC7\u6765\u4E86\uFF0C\u53C8\u8BF7\u4E86\u4E2A\u914D\u83DC\u5E08\uFF0C\u4E24\u4E2A\u914D\u83DC\u5E08\u5173\u7CFB\u662F\u96C6\u7FA4\u3002</p><h2 id="_2-\u5206\u5E03\u5F0F\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_2-\u5206\u5E03\u5F0F\u67B6\u6784" aria-hidden="true">#</a> 2. \u5206\u5E03\u5F0F\u67B6\u6784</h2><h3 id="_2-1-\u53EA\u6709web-server-\u662F\u5206\u5E03\u5F0F-\u96C6\u7FA4" tabindex="-1"><a class="header-anchor" href="#_2-1-\u53EA\u6709web-server-\u662F\u5206\u5E03\u5F0F-\u96C6\u7FA4" aria-hidden="true">#</a> 2.1 \u53EA\u6709web server \u662F\u5206\u5E03\u5F0F\uFF08\u96C6\u7FA4\uFF09</h3><blockquote><p>\u5BF9\u4E8E\u4E00\u822C\u5C0F\u578B\u7684\u7F51\u7AD9\u5176\u5B9E\u8DB3\u591F\u7528\u4E86\uFF0Cweb server \u96C6\u7FA4\uFF0C\u6570\u636E\u5E93db \u4E3B\u4ECE\u8BFB\u5199\u5206\u79BB</p></blockquote><p>\u5206\u5E03\u5F0F\u53EF\u7E41\u53EF\u7B80\uFF0C\u6700\u7B80\u5355\u7684\u5206\u5E03\u5F0F\u5C31\u662F\u5927\u5BB6\u6700\u5E38\u7528\u7684\uFF0C\u5728\u8D1F\u8F7D\u5747\u8861\u670D\u52A1\u5668\u540E\u52A0\u4E00\u5806web\u670D\u52A1\u5668\uFF0C\u7136\u540E\u5728\u4E0A\u9762\u641E\u4E00\u4E2A\u7F13\u5B58\u670D\u52A1\u5668\u6765\u4FDD\u5B58\u4E34\u65F6\u72B6\u6001\uFF0C\u540E\u9762\u5171\u4EAB\u4E00\u4E2A\u6570\u636E\u5E93\uFF0C\u5176\u5B9E\u5F88\u591A\u53F7\u79F0\u5206\u5E03\u5F0F\u4E13\u5BB6\u7684\u4EBA\u4E5F\u5C31\u505C\u7559\u4E8E\u6B64\uFF0C\u5927\u81F4\u7ED3\u6784\u5982\u4E0B\u56FE\u6240\u793A\uFF1A</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220616200046673.png" alt="image-20220616200046673" loading="lazy"></p><p>\u8FD9\u79CD\u73AF\u5883\u4E0B\u771F\u6B63\u8FDB\u884C\u5206\u5E03\u5F0F\u7684\u53EA\u662Fweb server\u800C\u5DF2\uFF0C\u5E76\u4E14web server\u4E4B\u95F4\u6CA1\u6709\u4EFB\u4F55\u8054\u7CFB\uFF0C\u6240\u4EE5\u7ED3\u6784\u548C\u5B9E\u73B0\u90FD\u975E\u5E38\u7B80\u5355\u3002</p><h3 id="_2-2-\u590D\u6742\u5206\u5E03\u5F0F\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#_2-2-\u590D\u6742\u5206\u5E03\u5F0F\u670D\u52A1" aria-hidden="true">#</a> 2.2 \u590D\u6742\u5206\u5E03\u5F0F\u670D\u52A1</h3><p>\u6709\u4E9B\u60C5\u51B5\u4E0B\uFF0C\u5BF9\u5206\u5E03\u5F0F\u7684\u9700\u6C42\u5C31\u6CA1\u8FD9\u4E48\u7B80\u5355\uFF0C<strong>\u5728\u6BCF\u4E2A\u73AF\u8282\u4E0A\u90FD\u6709\u5206\u5E03\u5F0F\u7684\u9700\u6C42</strong>\uFF0C\u6BD4\u5982Load Balance\u3001DB\u3001Cache\u548C\u6587\u4EF6\u7B49\u7B49\uFF0C\u5E76\u4E14\u5F53\u5206\u5E03\u5F0F\u8282\u70B9\u4E4B\u95F4\u6709\u5173\u8054\u65F6\uFF0C\u8FD8\u5F97\u8003\u8651\u4E4B\u95F4\u7684\u901A\u8BAF\uFF0C\u53E6\u5916\uFF0C\u8282\u70B9\u975E\u5E38\u591A\u7684\u65F6\u5019\uFF0C\u5F97\u6709\u76D1\u63A7\u548C\u7BA1\u7406\u6765\u652F\u6491\u3002</p><p>\u8FD9\u6837\u770B\u8D77\u6765\uFF0C\u5206\u5E03\u5F0F\u662F\u4E00\u4E2A\u975E\u5E38\u5E9E\u5927\u7684\u4F53\u7CFB\uFF0C\u53EA\u4E0D\u8FC7\u4F60\u53EF\u4EE5\u6839\u636E\u5177\u4F53\u9700\u6C42\u8FDB\u884C\u9002\u5F53\u5730\u88C1\u526A\u3002\u6309\u7167\u6700\u5B8C\u5907\u7684\u5206\u5E03\u5F0F\u4F53\u7CFB\u6765\u770B\uFF0C\u53EF\u4EE5\u7531\u4EE5\u4E0B\u6A21\u5757\u7EC4\u6210\uFF1A</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220616200940794.png" alt="image-20220616200940794" loading="lazy"></p><ul><li>**\u5206\u5E03\u5F0F\u4EFB\u52A1\u5904\u7406\u670D\u52A1\uFF1A**\u8D1F\u8D23\u5177\u4F53\u7684\u4E1A\u52A1\u903B\u8F91\u5904\u7406</li><li>**\u5206\u5E03\u5F0F\u8282\u70B9\u6CE8\u518C\u548C\u67E5\u8BE2\uFF1A**\u8D1F\u8D23\u7BA1\u7406\u6240\u6709\u5206\u5E03\u5F0F\u8282\u70B9\u7684\u547D\u540D\u548C\u7269\u7406\u4FE1\u606F\u7684\u6CE8\u518C\u4E0E\u67E5\u8BE2\uFF0C\u662F\u8282\u70B9\u4E4B\u95F4\u8054\u7CFB\u7684\u6865\u6881</li><li>**\u5206\u5E03\u5F0FDB\uFF1A**\u5206\u5E03\u5F0F\u7ED3\u6784\u5316\u6570\u636E\u5B58\u53D6</li><li>**\u5206\u5E03\u5F0FCache\uFF1A**\u5206\u5E03\u5F0F\u7F13\u5B58\u6570\u636E\uFF08\u975E\u6301\u4E45\u5316\uFF09\u5B58\u53D6</li><li>**\u5206\u5E03\u5F0F\u6587\u4EF6\uFF1A**\u5206\u5E03\u5F0F\u6587\u4EF6\u5B58\u53D6</li><li>**\u7F51\u7EDC\u901A\u4FE1\uFF1A**\u8282\u70B9\u4E4B\u95F4\u7684\u7F51\u7EDC\u6570\u636E\u901A\u4FE1</li><li>**\u76D1\u63A7\u7BA1\u7406\uFF1A**\u641C\u96C6\u3001\u76D1\u63A7\u548C\u8BCA\u65AD\u6240\u6709\u8282\u70B9\u8FD0\u884C\u72B6\u6001</li><li>**\u5206\u5E03\u5F0F\u7F16\u7A0B\u8BED\u8A00\uFF1A**\u7528\u4E8E\u5206\u5E03\u5F0F\u73AF\u5883\u4E0B\u7684\u4E13\u6709\u7F16\u7A0B\u8BED\u8A00\uFF0C\u6BD4\u5982Elang\u3001Scala</li><li>**\u5206\u5E03\u5F0F\u7B97\u6CD5\uFF1A**\u4E3A\u89E3\u51B3\u5206\u5E03\u5F0F\u73AF\u5883\u4E0B\u4E00\u4E9B\u7279\u6709\u95EE\u9898\u7684\u7B97\u6CD5\uFF0C\u6BD4\u5982\u89E3\u51B3\u4E00\u81F4\u6027\u95EE\u9898\u7684Paxos\u7B97\u6CD5</li></ul><p>\u56E0\u6B64\uFF0C\u82E5\u8981\u6DF1\u5165\u7814\u7A76\u4E91\u8BA1\u7B97\u548C\u5206\u5E03\u5F0F\uFF0C\u5C31\u5F97\u6DF1\u5165\u7814\u7A76\u4EE5\u4E0A\u9886\u57DF\uFF0C\u800C\u8FD9\u4E9B\u9886\u57DF\u6BCF\u4E00\u5757\u7684\u6C34\u90FD\u5F88\u6DF1\uFF0C\u90FD\u9700\u8981\u5F88\u5E95\u5C42\u7684\u77E5\u8BC6\u548C\u6280\u672F\u6765\u652F\u6491\uFF0C\u6240\u4EE5\u8BF4\uFF0C\u5BF9\u4E8E\u60F3\u63D0\u5347\u6280\u672F\u7684\u5F00\u53D1\u8005\u6765\u8BF4\uFF0C\u4EE5\u5206\u5E03\u5F0F\u6765\u4F5C\u4E3A\u5207\u5165\u70B9\u662F\u975E\u5E38\u597D\u7684\uFF0C\u53EF\u4EE5\u4EE5\u6B64\u4E3A\u7EBF\u7D22\uFF0C\u63A2\u7D22\u8BA1\u7B97\u673A\u4E16\u754C\u7684\u5404\u4E2A\u89D2\u843D\u3002</p><h2 id="_3-\u81EA\u5DF1\u5BF9\u5206\u5E03\u5F0F\u7684\u7406\u89E3" tabindex="-1"><a class="header-anchor" href="#_3-\u81EA\u5DF1\u5BF9\u5206\u5E03\u5F0F\u7684\u7406\u89E3" aria-hidden="true">#</a> 3. \u81EA\u5DF1\u5BF9\u5206\u5E03\u5F0F\u7684\u7406\u89E3</h2><p>\u5206\u5E03\u5F0F\u53EF\u4EE5\u6709\u4E24\u79CD\u8868\u73B0\u5F62\u5F0F\uFF0C</p><ul><li><p>\u4E00\u79CD\u662F\u62C6\u5206\u4E1A\u52A1\u7684\u5206\u5E03\u5F0FSOA , \u4EE5\u5FAE\u670D\u52A1\u4E3A\u4EE3\u8868</p><p>\u4F46\u4E0D\u9650\u4E8E\u5FAE\u670D\u52A1\u3002\u53EA\u8981\u62C6\u5206\u4E86\u5B50\u7CFB\u7EDF\u5C31\u7B97</p></li><li><p>\u4E00\u79CD\u662F\u4E0D\u62C6\u5206\u4E1A\u52A1\u7684\u5206\u5E03\u5F0F\u96C6\u7FA4\uFF0C\u53EA\u662F\u5C06\u670D\u52A1\u90E8\u7F72\u5728\u591A\u4E2A\u8282\u70B9</p></li></ul><p>\u8FD9\u4E24\u79CD\u90FD\u5C5E\u4E8E\u5206\u5E03\u5F0F\uFF0C\u53EA\u662F\u5212\u5206\u5F97\u4E0D\u4E00\u6837\u3002</p><h3 id="_3-1-\u82E5\u4F9D\u524D\u540E\u7AEF\u5206\u79BB\u7B97\u5206\u5E03\u5F0F\u5417" tabindex="-1"><a class="header-anchor" href="#_3-1-\u82E5\u4F9D\u524D\u540E\u7AEF\u5206\u79BB\u7B97\u5206\u5E03\u5F0F\u5417" aria-hidden="true">#</a> 3.1 \u82E5\u4F9D\u524D\u540E\u7AEF\u5206\u79BB\u7B97\u5206\u5E03\u5F0F\u5417\uFF1F</h3><p>\u8FD9\u8DDF\u524D\u540E\u7AEF\u5206\u79BB\u6CA1\u6709\u76F4\u63A5\u5173\u7CFB\uFF0C\u5C31\u770B\u90E8\u7F72\u7684\u5F62\u5F0F</p><ul><li>\u5355\u4F53\uFF1A\u4E00\u4E2A\u4E1A\u52A1\u670D\u52A1+\u4E00\u4E2Adb+\u4E00\u4E2Aredis</li><li>\u96C6\u7FA4\uFF1A\u591A\u4E2A\u4E1A\u52A1\u670D\u52A1+db\u4E3B\u4ECE\u5206\u79BB+\u4E3B\u4ECE\u5206\u79BBredis</li><li>\u5206\u5E03\u5F0F\uFF1A\u591A\u4E2A\u4E1A\u52A1\u670D\u52A1+\u591A\u4E2A\u4EFB\u52A1\u8C03\u5EA6\u670D\u52A1+\u76D1\u63A7\u670D\u52A1+\u7F13\u5B58\u670D\u52A1+\u591A\u4E2A\u6587\u4EF6\u670D\u52A1+\u591A\u4E2A\u6570\u636E\u5E93\u670D\u52A1</li></ul><h2 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h2>',27),p={href:"https://zhuanlan.zhihu.com/p/133508606",target:"_blank",rel:"noopener noreferrer"},c=o("\u5206\u5E03\u5F0F\u3001\u96C6\u7FA4\u3001\u5FAE\u670D\u52A1\u3001SOA \u4E4B\u95F4\u7684\u533A\u522B\uFF1F\uFF08\u8BE6\u89E3\uFF09");function g(u,_){const a=t("ExternalLinkIcon");return i(),l("div",null,[h,e("p",null,[e("a",p,[c,s(a)])])])}const f=r(d,[["render",g],["__file","distributed-ms-soa.html.vue"]]);export{f as default};
