const _hoisted_1 = /* @__PURE__ */ createStaticVNode('<h1 id="\u67B6\u6784-\u7406\u89E3\u6784\u67B6\u7684\u89C6\u89D2" tabindex="-1"><a class="header-anchor" href="#\u67B6\u6784-\u7406\u89E3\u6784\u67B6\u7684\u89C6\u89D2" aria-hidden="true">#</a> \u67B6\u6784-\u7406\u89E3\u6784\u67B6\u7684\u89C6\u89D2</h1><blockquote><p>\u5728\u5B66\u4E60\u67B6\u6784\u65F6\uFF0C\u6211\u8BA4\u4E3A\u9996\u5148\u8981\u7406\u6E05\u695A\u67B6\u6784\u7684\u89C6\u89D2\uFF0C<strong>\u56E0\u4E3A\u4F60\u6240\u8BA4\u77E5\u7684\u67B6\u6784\u548C\u522B\u4EBA\u6240\u8BF4\u7684\u67B6\u6784\u53EF\u80FD\u662F\u4E24\u7801\u4E8B</strong>\u3002\u5BF9\u4E8E\u4E0D\u540C\u804C\u4F4D\u7684\u89C6\u89D2\u662F\u4E0D\u4E00\u6837\u7684\uFF0C\u6BD4\u5982\u5F00\u53D1\u800C\u8A00\u4ED6\u66F4\u591A\u7684\u770B\u5230\u7684\u662F\u5F00\u53D1\u67B6\u6784\uFF1B\u5BF9\u552E\u524D\u4EBA\u5458\uFF0C\u4ED6\u53EF\u80FD\u66F4\u591A\u7684\u770B\u5230\u7684\u662F\u4E1A\u52A1\u67B6\u6784\uFF1B\u5BF9\u4E8E\u8FD0\u7EF4\u4EBA\u5458\uFF0C\u4ED6\u770B\u5230\u7684\u53EF\u80FD\u662F\u8FD0\u7EF4\u67B6\u6784\uFF1B\u800C\u5BF9\u4E8E\u6280\u672F\u652F\u6301\u548C\u90E8\u7F72\u4EBA\u5458\uFF0C\u4ED6\u66F4\u591A\u7684\u770B\u5230\u7684\u7F51\u7EDC\u548C\u7269\u7406\u67B6\u6784\u3002</p></blockquote><h2 id="_1-\u67B6\u6784\u7684\u89C6\u89D2" tabindex="-1"><a class="header-anchor" href="#_1-\u67B6\u6784\u7684\u89C6\u89D2" aria-hidden="true">#</a> 1. \u67B6\u6784\u7684\u89C6\u89D2</h2><p>\u5728\u7B14\u8005\u7684\u77E5\u8BC6\u4F53\u7CFB\u4E2D\uFF0C\u5B9E\u9645\u4E0A\u5C06\u67B6\u6784\u5206\u4E3A\u4E1A\u52A1\u67B6\u6784\u3001\u5E94\u7528\u67B6\u6784\u3001\u4E91\u57FA\u7840\u67B6\u6784\u8FD9\u51E0\u5927\u7C7B\uFF0C\u4E1A\u52A1\u67B6\u6784\u4E3B\u8981\u7740\u773C\u4E8E\u63A7\u5236\u4E1A\u52A1\u7684\u590D\u6742\u6027\uFF0C\u57FA\u7840\u67B6\u6784\u7740\u773C\u4E8E\u89E3\u51B3\u5206\u5E03\u5F0F\u7CFB\u7EDF\u4E2D\u5B58\u5728\u7684\u4E00\u7CFB\u5217\u95EE\u9898\u3002\u65E0\u8BBA\u4F55\u79CD\u67B6\u6784\uFF0C\u90FD\u5E0C\u671B\u80FD\u5B9E\u73B0\u7CFB\u7EDF\u7684\u53EF\u53D8\u7684\u540C\u65F6\u4FDD\u969C\u4E1A\u52A1\u7684\u9AD8\u53EF\u7528\u3002</p><blockquote><p>\u6CE8\u610F\uFF1A</p><ul><li>\u5F88\u591A\u65F6\u5019\u67B6\u6784\u7684\u89C6\u89D2/\u5206\u7C7B\u6CA1\u6709\u660E\u663E\u7684\u8FB9\u754C\uFF0C\u901A\u5E38\u662F\u4EA4\u53C9\u7684\uFF1B</li><li>\u6709\u610F\u601D\u7684\u662F\uFF0C\u8F6F\u4EF6\u67B6\u6784\u53CA\u5176\u89C6\u89D2\u5F80\u5F80\u548C\u5B83\u6240\u5728\u7684\u90E8\u95E8\u7EC4\u7EC7\u67B6\u6784\u6709\u7740\u76F4\u63A5\u5173\u7CFB)</li></ul></blockquote><h3 id="_1-1-\u4E1A\u52A1\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_1-1-\u4E1A\u52A1\u67B6\u6784" aria-hidden="true">#</a> 1.1 \u4E1A\u52A1\u67B6\u6784</h3><p>\u6838\u5FC3\u662F\u89E3\u51B3\u4E1A\u52A1\u5E26\u6765\u7684\u7CFB\u7EDF\u590D\u6742\u6027\uFF0C\u4E86\u89E3\u5BA2\u6237/\u4E1A\u52A1\u65B9\u7684\u75DB\u70B9\uFF0C\u9879\u76EE\u5B9A\u4E49\uFF0C\u73B0\u6709\u73AF\u5883\uFF1B\u68B3\u7406\u9AD8\u9636\u9700\u6C42\u548C\u975E\u529F\u80FD\u6027\u9700\u6C42\uFF0C\u8FDB\u884C\u95EE\u9898\u57DF\u5212\u5206\u4E0E\u9886\u57DF\u5EFA\u6A21\u7B49\u5DE5\u4F5C\uFF1B\u6C9F\u901A\uFF0C\u65B9\u6848\u5EFA\u8BAE\uFF0C\u591A\u6B21\u8FED\u4EE3\uFF0C\u4EA4\u4ED8\u603B\u4F53\u67B6\u6784\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704155449826.png" alt="image-20220704155449826"></p><p>\u770B\u770B\u4EAC\u4E1C\u4E1A\u52A1\u67B6\u6784\uFF08\u7F51\u4E0A\u5206\u4EAB\u56FE\uFF09\uFF1A</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704155536633.png" alt="image-20220704155536633"></p><h3 id="_1-2-\u5E94\u7528-\u6280\u672F\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_1-2-\u5E94\u7528-\u6280\u672F\u67B6\u6784" aria-hidden="true">#</a> 1.2 \u5E94\u7528/\u6280\u672F\u67B6\u6784</h3><p>\u6839\u636E\u4E1A\u52A1\u573A\u666F\u7684\u9700\u8981\uFF0C\u8BBE\u8BA1\u5E94\u7528\u7684\u5C42\u6B21\u7ED3\u6784\uFF0C\u5236\u5B9A\u5E94\u7528\u89C4\u8303\u3001\u5B9A\u4E49\u63A5\u53E3\u548C\u6570\u636E\u4EA4\u4E92\u534F\u8BAE\u7B49\u3002\u5E76\u5C3D\u91CF\u5C06\u5E94\u7528\u7684\u590D\u6742\u5EA6\u63A7\u5236\u5728\u4E00\u4E2A\u53EF\u4EE5\u63A5\u53D7\u7684\u6C34\u5E73\uFF0C\u4ECE\u800C\u5728\u5FEB\u901F\u7684\u652F\u6491\u4E1A\u52A1\u53D1\u5C55\u7684\u540C\u65F6\uFF0C\u5728\u4FDD\u8BC1\u7CFB\u7EDF\u7684\u53EF\u7528\u6027\u548C\u53EF\u7EF4\u62A4\u6027\u7684\u540C\u65F6\uFF0C\u786E\u4FDD\u5E94\u7528\u6EE1\u8DB3\u975E\u529F\u80FD\u5C5E\u6027\u8981\u6C42\uFF08\u6027\u80FD\u3001\u5B89\u5168\u3001\u7A33\u5B9A\u6027\u7B49\uFF09\u3002\u6280\u672F\u67B6\u6784\u4E3B\u8981\u8003\u8651\u7CFB\u7EDF\u7684\u975E\u529F\u80FD\u6027\u7279\u5F81\uFF0C\u5BF9\u7CFB\u7EDF\u7684\u9AD8\u53EF\u7528\u3001\u9AD8\u6027\u80FD\u3001\u6269\u5C55\u3001\u5B89\u5168\u3001\u4F38\u7F29\u6027\u3001\u7B80\u6D01\u7B49\u505A\u7CFB\u7EDF\u7EA7\u7684\u628A\u63E1\u3002</p><blockquote><p>\u4E0D\u9650\u4E8E\u5982\u4E0B\u89C6\u89D2\uFF0C\u4E3B\u8981\u8868\u793A\u5E94\u7528\u5F00\u53D1\u4E2D\u7684\u8F6F\u4EF6\u67B6\u6784\u89C6\u89D2...</p></blockquote><h4 id="_1-2-1\u89C6\u89D2-\u529F\u80FD\u89C6\u89D2" tabindex="-1"><a class="header-anchor" href="#_1-2-1\u89C6\u89D2-\u529F\u80FD\u89C6\u89D2" aria-hidden="true">#</a> 1.2.1\u89C6\u89D2\uFF1A\u529F\u80FD\u89C6\u89D2</h4><blockquote><p>\u529F\u80FD\u89C6\u89D2\u548C\u4E1A\u52A1\u89C6\u89D2\u6709\u91CD\u5408\u7684\u5730\u65B9\uFF0C\u4E3B\u8981\u9488\u5BF9\u5F00\u53D1\u800C\u8A00\u7684\u670D\u52A1\u529F\u80FD\uFF1B</p></blockquote><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704160030358.png" alt="image-20220704160030358"></p><h4 id="_1-2-2-\u89C6\u89D2-\u6280\u672F\u89C6\u89D2-\u603B\u4F53" tabindex="-1"><a class="header-anchor" href="#_1-2-2-\u89C6\u89D2-\u6280\u672F\u89C6\u89D2-\u603B\u4F53" aria-hidden="true">#</a> 1.2.2 \u89C6\u89D2\uFF1A\u6280\u672F\u89C6\u89D2-\u603B\u4F53</h4><p>\u6280\u672F\u6846\u67B6\uFF08technological Framework\uFF09\u662F\u6574\u4E2A\u6216\u90E8\u5206\u6280\u672F\u7CFB\u7EDF\u7684\u53EF\u91CD\u7528\u8BBE\u8BA1\uFF0C\u8868\u73B0\u4E3A\u4E00\u7EC4\u62BD\u8C61\u6784\u4EF6\u53CA\u6784\u4EF6\u5B9E\u4F8B\u95F4\u4EA4\u4E92\u7684\u65B9\u6CD5;\u53E6\u4E00\u79CD\u5B9A\u4E49\u8BA4\u4E3A\uFF0C\u6280\u672F\u6846\u67B6\u662F\u53EF\u88AB\u6280\u672F\u5F00\u53D1\u8005\u5B9A\u5236\u7684\u5E94\u7528\u9AA8\u67B6\u3002\u524D\u8005\u662F\u4ECE\u5E94\u7528\u65B9\u9762\u800C\u540E\u8005\u662F\u4ECE\u76EE\u7684\u65B9\u9762\u7ED9\u51FA\u7684\u5B9A\u4E49\u3002</p><h5 id="_1-2-2-1-\u793A\u4F8B\u4E00" tabindex="-1"><a class="header-anchor" href="#_1-2-2-1-\u793A\u4F8B\u4E00" aria-hidden="true">#</a> 1.2.2.1 \u793A\u4F8B\u4E00</h5><p>\u4ECE\u6280\u672F\u5C42\u9762\u63CF\u8FF0\uFF0C\u4E3B\u8981\u662F\u5206\u5C42\u6A21\u578B\uFF0C\u4F8B\u5982\u6301\u4E45\u5C42\u3001\u6570\u636E\u5C42\u3001\u903B\u8F91\u5C42\u3001\u5E94\u7528\u5C42\u3001\u8868\u73B0\u5C42\u7B49\uFF0C\u7136\u540E\u6BCF\u5C42\u4F7F\u7528\u4EC0\u4E48\u6280\u672F\u6846\u67B6\uFF0C\u4F8B\u5982Spring\u3001hibernate\u3001ioc\u3001MVC\u3001\u6210\u719F\u7684\u7C7B\u5E93\u3001\u4E2D\u95F4\u4EF6\u3001WebService\u7B49\uFF0C\u5206\u522B\u8BF4\u660E\uFF0C\u8981\u6C42\u8FD9\u4E9B\u6280\u672F\u80FD\u591F\u5C06\u6574\u4E2A\u7CFB\u7EDF\u7684\u4E3B\u8981\u5B9E\u73B0\u6982\u62EC\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704160212346.png" alt="image-20220704160212346"></p><h5 id="_1-2-2-2-\u793A\u4F8B\u4E8C-\u524D\u540E\u7AEF\u5206\u79BB" tabindex="-1"><a class="header-anchor" href="#_1-2-2-2-\u793A\u4F8B\u4E8C-\u524D\u540E\u7AEF\u5206\u79BB" aria-hidden="true">#</a> 1.2.2.2 \u793A\u4F8B\u4E8C\uFF1A \u524D\u540E\u7AEF\u5206\u79BB</h5><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704160339416.png" alt="image-20220704160339416"></p><h5 id="_1-2-2-2-\u793A\u4F8B\u4E09-\u5FAE\u670D\u52A1" tabindex="-1"><a class="header-anchor" href="#_1-2-2-2-\u793A\u4F8B\u4E09-\u5FAE\u670D\u52A1" aria-hidden="true">#</a> 1.2.2.2 \u793A\u4F8B\u4E09\uFF1A\u5FAE\u670D\u52A1</h5><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704160408430.png" alt="image-20220704160408430"></p><h4 id="_1-2-3-\u89C6\u89D2-\u6280\u672F\u89C6\u89D2-\u6570\u636E\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_1-2-3-\u89C6\u89D2-\u6280\u672F\u89C6\u89D2-\u6570\u636E\u67B6\u6784" aria-hidden="true">#</a> 1.2.3 \u89C6\u89D2\uFF1A\u6280\u672F\u89C6\u89D2-\u6570\u636E\u67B6\u6784</h4><p>\u4E13\u6CE8\u4E8E\u6784\u5EFA\u6570\u636E\u4E2D\u53F0\uFF0C\u7EDF\u4E00\u6570\u636E\u5B9A\u4E49\u89C4\u8303\uFF0C\u6807\u51C6\u5316\u6570\u636E\u8868\u8FBE\uFF0C\u5F62\u6210\u6709\u6548\u6613\u7EF4\u62A4\u7684\u6570\u636E\u8D44\u4EA7\u3002\u6253\u9020\u7EDF\u4E00\u7684\u5927\u6570\u636E\u5904\u7406\u5E73\u53F0\uFF0C\u5305\u62EC\u6570\u636E\u53EF\u89C6\u5316\u8FD0\u8425\u5E73\u53F0\u3001\u6570\u636E\u5171\u4EAB\u5E73\u53F0\u3001\u6570\u636E\u6743\u9650\u7BA1\u7406\u5E73\u53F0\u7B49\u3002</p><h4 id="_1-2-4-\u89C6\u89D2-\u6280\u672F\u89C6\u89D2-\u57FA\u7840\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_1-2-4-\u89C6\u89D2-\u6280\u672F\u89C6\u89D2-\u57FA\u7840\u67B6\u6784" aria-hidden="true">#</a> 1.2.4 \u89C6\u89D2\uFF1A\u6280\u672F\u89C6\u89D2-\u57FA\u7840\u67B6\u6784</h4><p>PAAS\uFF0CIAAS...</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704160603956.png" alt="image-20220704160603956"></p><h4 id="_12-5-\u89C6\u89D2-\u6280\u672F\u89C6\u89D2-\u8FD0\u7EF4\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_12-5-\u89C6\u89D2-\u6280\u672F\u89C6\u89D2-\u8FD0\u7EF4\u67B6\u6784" aria-hidden="true">#</a> 12.5 \u89C6\u89D2\uFF1A\u6280\u672F\u89C6\u89D2-\u8FD0\u7EF4\u67B6\u6784</h4><p>\u8D1F\u8D23\u8FD0\u7EF4\u7CFB\u7EDF\u7684\u89C4\u5212\u3001\u9009\u578B\u3001\u90E8\u7F72\u4E0A\u7EBF\uFF0C\u5EFA\u7ACB\u89C4\u8303\u5316\u7684\u8FD0\u7EF4\u4F53\u7CFB\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704160636044.png" alt="image-20220704160636044"></p><h3 id="_1-3-\u7269\u7406\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_1-3-\u7269\u7406\u67B6\u6784" aria-hidden="true">#</a> 1.3 \u7269\u7406\u67B6\u6784</h3><p>\u7269\u7406\u67B6\u6784\u5173\u6CE8\u8F6F\u4EF6\u5143\u4EF6\u662F\u5982\u4F55\u653E\u5230\u786C\u4EF6\u4E0A\u7684\uFF0C\u4E13\u6CE8\u4E8E\u57FA\u7840\u8BBE\u65BD\uFF0C\u67D0\u79CD\u8F6F\u786C\u4EF6\u4F53\u7CFB\uFF0C\u751A\u81F3\u4E91\u5E73\u53F0\uFF0C\u5305\u62EC\u673A\u623F\u642D\u5EFA\u3001\u7F51\u7EDC\u62D3\u6251\u7ED3\u6784\uFF0C\u7F51\u7EDC\u5206\u6D41\u5668\u3001\u4EE3\u7406\u670D\u52A1\u5668\u3001Web \u670D\u52A1\u5668\u3001\u5E94\u7528\u670D\u52A1\u5668\u3001\u62A5\u8868\u670D\u52A1\u5668\u3001\u6574\u5408\u670D\u52A1\u5668\u3001\u5B58\u50A8\u670D\u52A1\u5668\u548C\u4E3B\u673A\u7B49\u3002</p><h4 id="_1-3-1-\u4EE5\u4E00\u4E2A\u94F6\u884C\u7CFB\u7EDF\u4E3A\u4F8B" tabindex="-1"><a class="header-anchor" href="#_1-3-1-\u4EE5\u4E00\u4E2A\u94F6\u884C\u7CFB\u7EDF\u4E3A\u4F8B" aria-hidden="true">#</a> 1.3.1 \u4EE5\u4E00\u4E2A\u94F6\u884C\u7CFB\u7EDF\u4E3A\u4F8B</h4><p>\u4E0B\u9762\u4E3A\u4E1A\u52A1\u6027\u80FD\u53CA\u7F51\u7EDC\u6027\u80FD\u76D1\u63A7\u7684\u7269\u7406\u90E8\u7F72\u67B6\u6784\u56FE\uFF0C\u5206\u7F51\u7EDC\u63A5\u5165\u5C42\u548C\u6C47\u805A\u5C42\u4E24\u4E2A\u5C42\u6B21\u5BF9\u7F51\u7EDC\u6D41\u91CF\u62A5\u6587\u8FDB\u884C\u6355\u83B7\u548C\u6DF1\u5165\u5206\u6790\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704160722727.png" alt="image-20220704160722727"></p><p>\u7269\u7406\u90E8\u7F72\u67B6\u6784\u8BBE\u8BA1\u8BF4\u660E\uFF1A</p><ul><li>\uFF081\uFF09\u901A\u8FC74\u53F0TAP\u8BBE\u5907\u83B7\u53D6\u9752\u5C71\u6E56\u548C\u827E\u6EAA\u6E56\u4E24\u4E2A\u6570\u636E\u4E2D\u5FC3\u3001\u4E94\u4E2A\u673A\u623F\u76F8\u5173\u5E94\u7528\u670D\u52A1\u5668\u63A5\u5165\u4EA4\u6362\u673A\u7684\u955C\u50CF\u6D41\u91CF\uFF0C\u5E76\u8FDB\u884C\u89C4\u5219\u8FC7\u6EE4\uFF1B</li><li>\uFF082\uFF09\u901A\u8FC71\u53F0\u9AD8\u6027\u80FD\u6C47\u805ATAP\u6765\u83B7\u53D6\u827E\u6EAA\u6E56\u6570\u636E\u4E2D\u5FC3\u4E8C\u5C42\u6C47\u805A\u4EA4\u6362\u673A\u548C\u6838\u5FC3\u4EA4\u6362\u673A\u7684\u955C\u50CF\u6D41\u91CF\uFF0C\u5E76\u8FDB\u884C\u89C4\u5219\u8FC7\u6EE4\uFF1B</li><li>\uFF083\uFF09\u827E\u6EAA\u6E56\u4E3B\u6570\u636E\u4E2D\u5FC3\u5404\u673A\u623F\u63A5\u5165\u5C42TAP\u8BBE\u5907\u7684\u6D41\u91CF\u5171\u4EAB\u7ED9\u6C47\u805ATAP\u8BBE\u5907\uFF1B</li><li>\uFF084\uFF09BPC\u7CFB\u7EDF\u76845\u53F0BPC\u670D\u52A1\u5668\u5728\u4E24\u4E2A\u6570\u636E\u4E2D\u5FC3\u7684\u6BCF\u4E2A\u673A\u623F\u8FDB\u884C\u5206\u5E03\u5F0F\u90E8\u7F72\u3001\u89E3\u7801\u548C\u5206\u6790\uFF0C\u5E76\u96C6\u4E2D\u5C55\u793A\uFF1B</li><li>\uFF085\uFF09NPM\u7CFB\u7EDF\u5728\u827E\u6EAA\u6E56\u6570\u636E\u4E2D\u5FC3\u90E8\u7F72\u4E00\u53F0\u7BA1\u7406\u7AEF\u670D\u52A1\u5668\uFF0C\u5E76\u5728\u6BCF\u4E2A\u6570\u636E\u4E2D\u5FC3\u5404\u90E8\u7F72\u4E00\u53F0NPM\u63A2\u9488\u670D\u52A1\u5668\uFF0C\u901A\u8FC7\u5206\u5E03\u5F0F\u90E8\u7F72\u3001\u6355\u83B7\u6570\u636E\uFF0C\u96C6\u4E2D\u76D1\u63A7\u5C55\u793A\u7684\u65B9\u5F0F\uFF0C\u76D1\u63A7\u4E24\u4E2A\u6570\u636E\u4E2D\u5FC3\u7684\u5404\u4E1A\u52A1\u7CFB\u7EDF\u7684\u7F51\u7EDC\u6027\u80FD\uFF1B</li><li>\uFF086\uFF09\u901A\u8FC7\u53CC\u6570\u636E\u4E2D\u5FC3\u3001\u591A\u673A\u623F\u5206\u5E03\u5F0F\u90E8\u7F72\u7684\u65B9\u5F0F\uFF0C\u7AEF\u5230\u7AEF\u7684\u76D1\u63A7\u4E1A\u52A1\u5728\u5404\u4E2A\u73AF\u8282\u7684\u6D41\u8F6C\u60C5\u51B5\uFF0C\u5B9E\u65F6\u76D1\u63A7\uFF0C\u5FEB\u901F\u5B9A\u4F4D\u3002</li></ul><p>\u4E0B\u9762\u4E3A\u8FD0\u7EF4\u5927\u6570\u636E\u5E73\u53F0\u7684\u7269\u7406\u90E8\u7F72\u62D3\u6251\u56FE\uFF0C\u5206\u4E3A\u4E09\u4E2A\u96C6\u7FA4\uFF0CHadoop\u96C6\u7FA4\u3001ES\u65E5\u5FD7\u96C6\u7FA4\u548CKalfka\u6D88\u606F\u96C6\u7FA4\u3002</p><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704160844858.png" alt="image-20220704160844858"></p><p>\u7269\u7406\u90E8\u7F72\u67B6\u6784\u8BBE\u8BA1\u8BF4\u660E\uFF1A</p><ul><li>\u914D\u7F6E\u591A\u53F0\u670D\u52A1\u5668\u505AHadoop\u96C6\u7FA4\uFF0C\u6EE1\u8DB3\u4E0D\u540C\u5E94\u7528\u548C\u7CFB\u7EDF\u65E5\u5FD7\u7684\u5355\u7CFB\u7EDF\u4E0E\u8DE8\u7CFB\u7EDF\u4EA4\u6613\u65E5\u5FD7\u7EDF\u8BA1\u4E0E\u5206\u6790\uFF0C\u6EE1\u8DB3\u6570\u5343\u4E2A\u57FA\u7840\u76D1\u63A7\u5206\u533A\u7684\u57FA\u7840\u6027\u80FD\u5206\u6790\u4E0E\u8FD0\u884C\u6027\u80FD\u6307\u6807\u9884\u6D4B\u7B49\uFF0C\u4EE5\u53CA\u6307\u6027\u80FD\u6807\u5165\u5E93\u4E0E\u5386\u53F2\u65E5\u5FD7\u6570\u636E\u5165\u5E93\u7684\u5B58\u50A8\u9700\u8981\u3002</li><li>\u914D\u7F6E\u591A\u53F0\u670D\u52A1\u5668\u505AES\u96C6\u7FA4\uFF0C\u627F\u8F7D\u5B9E\u65F6\u7EDF\u4E00\u65E5\u5FD7\u67E5\u8BE2\u4E0E\u5206\u6790\u5E73\u53F0\u7684\u4EFB\u52A1\uFF0C\u6EE1\u8DB3\u6570\u5929\u81F3\u4E00\u4E2A\u6708\u4E0D\u540C\u9700\u6C42\u7684\u65E5\u5FD7\u67E5\u8BE2\u548C\u5206\u6790\u9700\u6C42\uFF0C\u5386\u53F2\u65E5\u5FD7\u67E5\u8BE2\u9700\u8981\u4ECEHDFS\u4E2D\u5C06\u6570\u636E\u5BFC\u5165\u81F3ES\u4E2D\uFF0C\u8FDB\u884C\u4E8C\u6B21\u67E5\u8BE2\u3002</li><li>\u914D\u7F6E\u591A\u53F0\u670D\u52A1\u5668\u505AKafka\u96C6\u7FA4\u7528\u4E8E\u5B9E\u65F6\u7684\u6307\u6807\u578B\u4E0E\u65E5\u5FD7\u578B\u6570\u636E\u6D41\u7684\u91C7\u96C6\uFF0C\u6EE1\u8DB3\u5B9E\u65F6\u76D1\u63A7\u7684\u9700\u6C42\u3002</li></ul><h2 id="_2-ddd\u5230\u5404\u79CD\u67B6\u6784" tabindex="-1"><a class="header-anchor" href="#_2-ddd\u5230\u5404\u79CD\u67B6\u6784" aria-hidden="true">#</a> 2. DDD\u5230\u5404\u79CD\u67B6\u6784</h2><p>\u9886\u57DF\u9A71\u52A8\u8BBE\u8BA1\u7684\u6218\u7565\u6838\u5FC3\u5373\u662F\u5C06\u95EE\u9898\u57DF\u4E0E\u5E94\u7528\u67B6\u6784\u76F8\u5265\u79BB\uFF0C\u5C06\u4E1A\u52A1\u8BED\u4E49\u663E\u73B0\u5316\uFF0C\u628A\u539F\u5148\u6666\u6DA9\u96BE\u61C2\u7684\u4E1A\u52A1\u7B97\u6CD5\u903B\u8F91\uFF0C\u901A\u8FC7\u9886\u57DF\u5BF9\u8C61\uFF08Domain Object\uFF09\uFF0C\u7EDF\u4E00\u8BED\u8A00\uFF08Ubiquitous Language\uFF09\u8F6C\u5316\u4E3A\u9886\u57DF\u6982\u5FF5\u6E05\u6670\u7684\u663E\u6027\u5316\u8868\u8FBE\u51FA\u6765\u3002</p><ul><li><strong>\u7EDF\u4E00\u8BED\u8A00</strong>\uFF0C\u8F6F\u4EF6\u7684\u5F00\u53D1\u4EBA\u5458/\u4F7F\u7528\u4EBA\u5458\u90FD\u4F7F\u7528\u540C\u4E00\u5957\u8BED\u8A00\uFF0C\u5373\u5BF9\u67D0\u4E2A\u6982\u5FF5\uFF0C\u540D\u8BCD\u7684\u8BA4\u77E5\u662F\u7EDF\u4E00\u7684\uFF0C\u5EFA\u7ACB\u6E05\u6670\u7684\u4E1A\u52A1\u6A21\u578B\uFF0C\u5F62\u6210\u7EDF\u4E00\u7684\u4E1A\u52A1\u8BED\u4E49\u3002\u5C06\u6A21\u578B\u4F5C\u4E3A\u8BED\u8A00\u7684\u652F\u67F1\u3002\u786E\u4FDD\u56E2\u961F\u5728\u5185\u90E8\u7684\u6240\u6709\u4EA4\u6D41\u4E2D\uFF0C\u4EE3\u7801\u4E2D\uFF0C\u753B\u56FE\uFF0C\u5199\u4E1C\u897F\uFF0C\u7279\u522B\u662F\u8BB2\u8BDD\u7684\u65F6\u5019\u90FD\u8981\u4F7F\u7528\u8FD9\u79CD\u8BED\u8A00\u3002\u4F8B\u5982\u8D26\u53F7\uFF0C\u8F6C\u8D26\uFF0C\u900F\u652F\u7B56\u7565\uFF0C\u8FD9\u4E9B\u90FD\u662F\u975E\u5E38\u91CD\u8981\u7684\u9886\u57DF\u6982\u5FF5\uFF0C\u5982\u679C\u8FD9\u4E9B\u547D\u540D\u90FD\u548C\u6211\u4EEC\u65E5\u5E38\u8BA8\u8BBA\u4EE5\u53CA PRD \u4E2D\u7684\u63CF\u8FF0\u4FDD\u6301\u4E00\u81F4\uFF0C\u5C06\u4F1A\u6781\u5927\u63D0\u5347\u4EE3\u7801\u7684\u53EF\u8BFB\u6027\uFF0C\u51CF\u5C11\u8BA4\u77E5\u6210\u672C\u3002\u3002\u6BD4\u5982\u4E0D\u518D\u4F1A\u6709\u4EBA\u5728\u4F1A\u8BAE\u4E2D\u5BF9\u201C\u5DE5\u5355\u201D\u3001\u201C\u5BA1\u6838\u5355\u201D\u3001\u201C\u8868\u5355\u201D\u800C\u53CD\u590D\u786E\u8BA4\u542B\u4E49\u4E86\uFF0CDDD \u7684\u6A21\u578B\u5EFA\u7ACB\u4E0D\u4F1A\u88AB DB \u6240\u7ED1\u67B6\u3002</li><li><strong>\u9762\u5411\u9886\u57DF</strong>\uFF0C\u4E1A\u52A1\u8BED\u4E49\u663E\u6027\u5316\uFF0C\u4EE5\u9886\u57DF\u53BB\u601D\u8003\u95EE\u9898\uFF0C\u800C\u4E0D\u662F\u6A21\u5757\u3002\u5C06\u9690\u5F0F\u7684\u4E1A\u52A1\u903B\u8F91\u4ECE\u4E00\u63A8 if-else \u91CC\u9762\u62BD\u53D6\u51FA\u6765\uFF0C\u7528\u901A\u7528\u8BED\u8A00\u53BB\u547D\u540D\u3001\u53BB\u5199\u4EE3\u7801\u3001\u53BB\u6269\u5C55\uFF0C\u8BA9\u5176\u53D8\u6210\u663E\u793A\u6982\u5FF5\uFF1B\u5F88\u591A\u91CD\u8981\u7684\u4E1A\u52A1\u6982\u5FF5\uFF0C\u6309\u7167\u4E8B\u52A1\u811A\u672C\u7684\u5199\u6CD5\uFF0C\u5176\u542B\u4E49\u5B8C\u5168\u6DF9\u6CA1\u5728\u4EE3\u7801\u903B\u8F91\u4E2D\u6CA1\u6709\u7A81\u663E\u51FA\u6765\u3002</li><li><strong>\u804C\u8D23\u5212\u5206</strong>\uFF0C\u6839\u636E\u5B9E\u9645\u4E1A\u52A1\u5408\u7406\u5212\u5206\u6A21\u578B\uFF0C\u6A21\u578B\u4E4B\u95F4\u4F9D\u8D56\u7ED3\u6784\u548C\u8FB9\u754C\u66F4\u52A0\u6E05\u6670\uFF0C\u907F\u514D\u4E86\u6DF7\u4E71\u7684\u4F9D\u8D56\u5173\u7CFB\uFF0C\u8FDB\u800C\u589E\u52A0\u53EF\u8BFB\u6027\u3001\u53EF\u7EF4\u62A4\u6027\uFF1B\u5355\u4E00\u804C\u8D23\uFF0C\u6A21\u578B\u53EA\u5173\u6CE8\u81EA\u8EAB\u7684\u672C\u804C\u5DE5\u4F5C\uFF0C\u907F\u514D\u201C\u8D8A\u6743\u201D\u800C\u5BFC\u81F4\u6DF7\u4E71\u7684\u8C03\u7528\u5173\u7CFB\u3002\u901A\u8FC7\u5EFA\u6A21\uFF0C\u66F4\u597D\u7684\u8868\u8FBE\u73B0\u5B9E\u4E16\u754C\u4E2D\u7684\u590D\u6742\u4E1A\u52A1\uFF0C\u968F\u7740\u65F6\u95F4\u7684\u53D1\u5C55\uFF0C\u4E0D\u65AD\u589E\u52A0\u7CFB\u7EDF\u5BF9\u5B9E\u9645\u4E1A\u52A1\u7684\u6C89\u6DC0\uFF0C\u4E5F\u5C06\u66F4\u597D\u7684\u901A\u8FC7\u6E05\u6670\u7684\u4EE3\u7801\u63CF\u8FF0\u4E1A\u52A1\u903B\u8F91\uFF0C\u6A21\u578B\u7684\u5185\u805A\u589E\u52A0\u4E86\u7CFB\u7EDF\u7684\u9AD8\u5EA6\u6A21\u5757\u5316\uFF0C\u63D0\u5347\u4EE3\u7801\u7684\u53EF\u91CD\u7528\u6027\uFF0C\u5BF9\u6BD4\u4F20\u7EDF\u4E09\u5C42\u6A21\u5F0F\u4E2D\uFF0C\u5F88\u6709\u53EF\u80FD\u5927\u91CF\u91CD\u590D\u7684\u529F\u80FD\u6563\u843D\u5728\u5404\u4E2A Service \u5185\u90E8\u3002</li></ul><p><img src="https://zszblog.oss-cn-beijing.aliyuncs.com/zszblog/image-20220704171935631.png" alt="image-20220704171935631"></p><h2 id="\u53C2\u8003\u6587\u7AE0" tabindex="-1"><a class="header-anchor" href="#\u53C2\u8003\u6587\u7AE0" aria-hidden="true">#</a> \u53C2\u8003\u6587\u7AE0</h2>', 49);
